  render() {
    console.log();
    const { questions, options, currentQuestion } = this.state;
    //  console.log(questions[currentQuestion].question);
    return (
      <div className="Quiz">
        {questions[currentQuestion].question}
        <button onClick={this.nextQuestion}>Next</button>
      </div>
    );
  }
}

export default Quiz;
