import React, { Component } from "react";
import QuizComponent from "../QuizComponent/QuizComponent";
import { questions } from "../../questions.js";
import "./Quiz.css";
class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userAnswer: null,
      currentQuestion: 0,
    };
  }

  loadQuiz = () => {
    const { currentQuestion } = this.state;
    this.setState(() => {
      return {
        questions: questions[currentQuestion].question,
      };
    });
  };

  render() {
    return (
      <div className="Quiz">
        <div>
          <div className="buttons-display">
            <button onClick={this.sakte}>Sakte</button>
            <button onClick={this.pasakte}>Pa Sakte</button>
          </div>
          <p>Total te qelluara: {this.state.score}</p>
          <button onClick={this.resetGame}>Rifillo</button>
          <div>
            <p>Pytje Aktuale:4/10</p>
            <p>Total Pyetje:{this.state.pyetjet.length}</p>
          </div>
          <div>Progres bar</div>
        </div>
      </div>
    );
  }
}

export default Quiz;
