import React, { Component } from "react";

class Button extends Component {
  constructor(props) {
    super(props);
    this.state = {};

    //   this.checkAnswer = this.checkAnswer.bind(this);
  }
  render() {
    return (
      <div>
        <button
          style={{ backgroundColor: "#FF7F27", color: "black" }}
          onClick={() => this.props.checkAnswer}
        >
          {this.props.name}
        </button>
      </div>
    );
  }
}

export default Button;
