import React, { Component } from "react";
import QuizComponent from "../QuizComponent/QuizComponent";
import { QuizData } from "../../questions.js";
import "./Quiz.css";
class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userAnswer: null,
      currentQuestion: 0,
      options: [],
      questions: QuizData,
      quizEnd: false,
      score: 0,
      disabled: true,
    };
  }

  nextQuestion = () => {
    const { userAnswer, answer, score } = this.state;
    this.setState({
      currentQuestion: this.state.currentQuestion + 1,
      disabled: true,
    });
    if (userAnswer === answer) {
      this.setState({
        score: score + 1,
      });
    }
    console.log(answer);
  };

  //check answer
  checkAnswer = (answer) => {
    this.setState({
      userAnswer: answer,
      disabled: false,
    });
  };

  finishGame = () => {
    if (this.state.currentQuestion === QuizData.length - 1) {
      this.setState({
        quizEnd: true,
      });
    }
  };

  render() {
    console.log();
    const {
      questions,
      options,
      currentQuestion,
      userAnswer,
      quizEnd,
      disabled,
    } = this.state;

    if (quizEnd) {
      return (
        <div>
          <h2>Game has ended</h2>
        </div>
      );
    }

    //console.log(currentQuestion);
    return (
      <div>
        <span>{`Question ${currentQuestion}/${QuizData.length}`}</span>
        <div>Score is :{this.state.score}</div>

        <div>{questions[currentQuestion].question}</div>
        <div>
          <button
            onClick={() =>
              this.checkAnswer(questions[currentQuestion].options[0])
            }
          >
            {questions[currentQuestion].options[0]}
          </button>
          <button
            onClick={() =>
              this.checkAnswer(questions[currentQuestion].options[1])
            }
          >
            {questions[currentQuestion].options[1]}
          </button>
        </div>

        {currentQuestion < QuizData.length - 1 && (
          <button disabled={this.state.disabled} onClick={this.nextQuestion}>
            Next
          </button>
        )}
        {currentQuestion === QuizData.length - 1 && (
          <button onClick={this.finishGame}>Finish</button>
        )}
      </div>
    );
  }
}

export default Quiz;
