import React, { Component } from "react";
import QuizComponent from "../QuizComponent/QuizComponent";
import { QuizData } from "../../questions.js";
import ProgressBar from "../../ProgressBar/ProgressBar";
import "./Quiz.css";
class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userAnswer: null,
      currentQuestion: 0,
      // options: [],
      questions: QuizData,
      quizEnd: false,
      score: 0,
      disabled: true,
      width: 0,
    };
  }

  nextQuestion = () => {
    const {
      userAnswer,
      answer,
      score,
      questions,
      width,
      currentQuestion,
    } = this.state;

    this.setState({
      currentQuestion: this.state.currentQuestion + 1,
      disabled: true,
      width: width + 10,
    });
    if (userAnswer === questions[currentQuestion].answer) {
      this.setState({
        score: score + 1,
      });
    }
    console.log(questions[currentQuestion].answer);
    console.log(userAnswer);
  };

  //check answer
  checkAnswer = (answer) => {
    this.setState({
      userAnswer: answer,
      disabled: false,
    });
  };

  rifilloLojen = () => {
    this.setState({
      userAnswer: null,
      currentQuestion: 0,
      questions: QuizData,
      quizEnd: false,
      score: 0,
      disabled: true,
      width: 0,
    });
  };

  render() {
    console.log();
    const {
      questions,
      width,
      options,
      currentQuestion,
      userAnswer,
      quizEnd,
      disabled,
    } = this.state;

    if (quizEnd) {
      return (
        <div>
          <h2>Game has ended</h2>
        </div>
      );
    }

    //console.log(currentQuestion);
    return (
      <div className="Quiz">
        <div>{questions[currentQuestion].question}</div>
        <div>
          <button
            onClick={() =>
              this.checkAnswer(questions[currentQuestion].options[0])
            }
          >
            {questions[currentQuestion].options[0]}
          </button>
          <button
            onClick={() =>
              this.checkAnswer(questions[currentQuestion].options[1])
            }
          >
            {questions[currentQuestion].options[1]}
          </button>
        </div>

        {currentQuestion < questions.length && (
          <button disabled={this.state.disabled} onClick={this.nextQuestion}>
            Next
          </button>
        )}
        <h6>Total te qelluara :{this.state.score}</h6>
        <button onClick={this.rifilloLojen}>Rifillo</button>
        <div className="progress-bar">
          <span>{`Pyetja Aktuale ${currentQuestion + 1}/${
            questions.length
          }`}</span>
          <div style={{ float: "right" }}>
            Total Pyetje :{this.state.questions.length}
          </div>

          <ProgressBar width={width} />
        </div>
      </div>
    );
  }
}

export default Quiz;
