import React, { Component } from "react";
import QuizComponent from "../QuizComponent/QuizComponent";

class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      round: 0,
      pyetjet: [
        {
          id: 1,
          pyetja: "Niagara eshte ujvara me e larta ne bote",
          pergjigja: false,
        },
        {
          id: 2,
          pyetja: "Pavarsia e Kosoves eshte me 17 shkurt ",
          pergjigja: true,
        },
      ],
    };
    this.sakte = this.sakte.bind(this);
    this.pasakte = this.pasakte.bind(this);
  }

  sakte(e) {
    alert(e);
  }

  pasakte(e) {}

  render() {
    return (
      <div>
        <div>
          {this.state.pyetjet.map((pyetja, index) => {
            return <QuizComponent key={index} pyetja={pyetja.pyetja} />;
          })}

          <div>
            <button onClick={this.sakte}>Sakte</button>
            <button onClick={this.pasakte}>Pasakte</button>
          </div>
          <p>Total te qelluara: 5</p>
          <button onClick={this.resetGame}>Rifillo</button>
          <div>
            <p>Pytje Aktuale:4/10</p>
            <p>Total Pyetje:{this.state.pyetjet.length}</p>
          </div>
          <div>Progres bar</div>
        </div>
      </div>
    );
  }
}

export default Quiz;
