import React, { Component } from "react";
import QuizComponent from "../QuizComponent/QuizComponent";
import { QuizData } from "../../questions.js";
import "./Quiz.css";
class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userAnswer: null,
      currentQuestion: 0,
      options: QuizData,
      questions: QuizData,
      quizEnd: false,
      score: 0,
      disabled: true,
    };
  }

  nextQuestion = () => {
    this.setState({
      currentQuestion: this.state.currentQuestion + 1,
    });
  };

  //check answer
  checkAnswer = (answer) => {
    this.setState({
      userAnswer: answer,
    });
  };

  finishGame = () => {
    if (this.state.currentQuestion === QuizData.length - 1) {
      this.setState({
        quizEnd: true,
      });
    }
  };

  render() {
    console.log();
    const {
      questions,
      options,
      currentQuestion,
      userAnswer,
      quizEnd,
    } = this.state;

    if (quizEnd) {
      return (
        <div>
          <h2>Game has ended</h2>
        </div>
      );
    }

    console.log(currentQuestion);
    return (
      <div>
        <span>{`Question ${currentQuestion}/${QuizData.length}`}</span>

        <div>{questions[currentQuestion].question}</div>
        <div>
          <button
            onClick={() =>
              this.checkAnswer(questions[currentQuestion].options[0])
            }
          >
            {questions[currentQuestion].options[0]}
          </button>
          <button onClick={() => this.checkAnswer(options)}>
            {questions[currentQuestion].options[1]}
          </button>
        </div>

        {currentQuestion < QuizData.length - 1 && (
          <button onClick={this.nextQuestion}>Next</button>
        )}
        {currentQuestion === QuizData.length - 1 && (
          <button onClick={this.finishGame}>Finish</button>
        )}
      </div>
    );
  }
}

export default Quiz;
