import React, { Component } from "react";
import QuizComponent from "../QuizComponent/QuizComponent";
import { QuizData } from "../../questions.js";
import ProgressBar from "../../ProgressBar/ProgressBar";
import "./Quiz.css";
import Button from "../Buttons/Button";
class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userAnswer: false,
      currentQuestion: 0,
      questions: QuizData,
      quizEnd: false,
      score: 0,
      width: 0,
    };
  }

  checkAnswer = (answer) => {
    const { userAnswer, score, questions, width, currentQuestion } = this.state;

    if (questions[currentQuestion].id < questions.length) {
      this.setState({
        currentQuestion: this.state.currentQuestion + 1,
        width: width + 10,
      });

      if (answer === questions[currentQuestion].answer) {
        this.setState({
          score: score + 1,
        });
      } else {
        this.setState({ quizEnd: true });
      }
    }
  };

  rifilloLojen = () => {
    this.setState({
      userAnswer: null,
      currentQuestion: 0,
      questions: QuizData,
      quizEnd: false,
      score: 0,
      width: 0,
    });
  };

  render() {
    const {
      questions,
      width,
      currentQuestion,
      userAnswer,
      quizEnd,
    } = this.state;

    if (quizEnd) {
      return <h2>liman</h2>;
    }
    return (
      <div className="Quiz">
        <h4>Pyetja: {questions[currentQuestion].id}</h4>
        <h3>{questions[currentQuestion].question}</h3>
        <div className="butonat">
          <Button
            name="Sakte"
            ngjyra="#22B14C"
            color="white"
            metoda={() =>
              this.checkAnswer(questions[currentQuestion].options[0])
            }
          />
          <Button
            name="Pa sakte"
            ngjyra="#ED1C24"
            color="white"
            metoda={() =>
              this.checkAnswer(questions[currentQuestion].options[1])
            }
          />
        </div>
        <h3>Total te qelluara :{this.state.score}</h3>
        <Button
          name="Rifillo"
          ngjyra="#FF7F27"
          color="black"
          metoda={this.rifilloLojen}
        />
        <div className="progress-bar">
          <span>{`Pyetja Aktuale ${currentQuestion + 1}/${
            questions.length
          }`}</span>
          <div style={{ float: "right" }}>
            Total Pyetje :{this.state.questions.length}
          </div>

          <ProgressBar width={width} />
        </div>
      </div>
    );
  }
}

export default Quiz;
