import React, { Component } from "react";
import QuizComponent from "../QuizComponent/QuizComponent";
import "../../questions.js";
import "./Quiz.css";
class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userAnswer: null,
      currentQuestion: 0,
    };
    this.sakte = this.sakte.bind(this);
    this.pasakte = this.pasakte.bind(this);
    this.resetGame = this.resetGame.bind(this);
  }

  sakte(e) {}

  pasakte(e) {}

  resetGame() {
    alert("sadsad");
  }

  render() {
    return (
      <div className="Quiz">
        <div>
          {this.state.pyetjet.map((pyetja, index) => {
            return (
              <QuizComponent key={index} id={index} pyetja={pyetja.pyetja} />
            );
          })}

          <div className="buttons-display">
            <button onClick={this.sakte}>Sakte</button>
            <button onClick={this.pasakte}>Pa Sakte</button>
          </div>
          <p>Total te qelluara: {this.state.score}</p>
          <button onClick={this.resetGame}>Rifillo</button>
          <div>
            <p>Pytje Aktuale:4/10</p>
            <p>Total Pyetje:{this.state.pyetjet.length}</p>
          </div>
          <div>Progres bar</div>
        </div>
      </div>
    );
  }
}

export default Quiz;
