import React, { Component } from "react";
import QuizComponent from "../QuizComponent/QuizComponent";
import { QuizData } from "../../questions.js";
import ProgressBar from "../../ProgressBar/ProgressBar";
import "./Quiz.css";
import Button from "../Buttons/Button";
class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userAnswer: false,
      currentQuestion: 0,
      questions: QuizData,
      quizEnd: false,
      score: 0,
      disabled: true,
      width: 0,
    };
  }

  checkAnswer = (answer) => {
    console.log("answer", answer);
    const { userAnswer, score, questions, width, currentQuestion } = this.state;
    console.log(userAnswer);
    console.log("Para useranswer", userAnswer);
    this.setState({
      userAnswer: answer,
    });
    console.log("Pas useranswer", userAnswer);
    this.setState({
      currentQuestion: this.state.currentQuestion + 1,
      disabled: true,
      width: width + 10,
    });
    if (userAnswer === questions[currentQuestion].answer) {
      this.setState({
        score: score + 1,
      });
    }
    if (questions[currentQuestion].id > questions.length) {
      this.setState({ quizEnd: true });
    }
    // console.log("Pergjigja", questions[currentQuestion].answer);
    // console.log(questions.length);
    // console.log(questions[currentQuestion].id);
  };

  rifilloLojen = () => {
    this.setState({
      userAnswer: null,
      currentQuestion: 0,
      questions: QuizData,
      quizEnd: false,
      score: 0,
      disabled: true,
      width: 0,
    });
  };

  render() {
    const {
      questions,
      width,
      options,
      currentQuestion,
      userAnswer,
      quizEnd,
      disabled,
    } = this.state;

    if (quizEnd) {
      return (
        <div>
          <h2>Game has ended</h2>
        </div>
      );
    }

    return (
      <div className="Quiz">
        <h4>Pyetja: {questions[currentQuestion].id}</h4>
        <h3>{questions[currentQuestion].question}</h3>
        <div>
          <button
            style={{ backgroundColor: "#22B14C", color: "white" }}
            onClick={() =>
              this.checkAnswer(questions[currentQuestion].options[0])
            }
          >
            {/* {questions[currentQuestion].options[0]} */}
            Sakte
          </button>
          {/* <Button
            name="True"
            checkAnswer={() =>
              this.checkAnswer(questions[currentQuestion].options[0])
            }
          /> */}
          <button
            style={{ backgroundColor: "#ED1C24", color: "white" }}
            onClick={() =>
              this.checkAnswer(questions[currentQuestion].options[1])
            }
          >
            {/* {questions[currentQuestion].options[1]} */}
            Pasakte
          </button>
        </div>

        <h3>Total te qelluara :{this.state.score}</h3>
        <button
          style={{ backgroundColor: "#FF7F27", color: "black" }}
          onClick={this.rifilloLojen}
        >
          Rifillo
        </button>
        <div className="progress-bar">
          <span>{`Pyetja Aktuale ${currentQuestion + 1}/${
            questions.length
          }`}</span>
          <div style={{ float: "right" }}>
            Total Pyetje :{this.state.questions.length}
          </div>

          <ProgressBar width={width} />
        </div>
      </div>
    );
  }
}

export default Quiz;
